// CSS
import './App.css';
// Components
import StartScreem from './components/StartScreem';
import Game from './components/Game'
import GameOver from './components/GameOver';
//hooks
import { useCallback, useEffect, useState } from "react";
//data
import { wordsList } from "./data/Words";

const stages = [
  {id: 1, name: "start"},
  {id: 2, name: "game"},
  {id: 3, name: "end"},
];

function App() {

  const [gameStage, setGameStage] = useState(stages[0].name);
  const [words] = useState(wordsList);


  const [picedkWord, setPicedkWord] = useState("");
  const [pickedCategory, setPickedCategory] = useState("");
  const [letters, setLetters] = useState([]);

  const pickWorldAndCategories = () => {
    const categories = Object.keys(words);
    const category = categories[Math.floor(Math.random() * Object.keys(categories).lenght)];      
  }

  // Startando Secret World game
  const startGame = () => {
    //Picked word and picked category
    pickWorldAndCategories();

    setGameStage(stages[1].name)
  }
  //Finalizando com Game Over
  const verifyLetter = () => {
    setGameStage(stages[2].name);
  };
  //Retry game
  const retry = () => {
    setGameStage(stages[0].name);
  }

  return (
    <div className="App">
      {gameStage === 'start' && <StartScreem startGame={startGame}/>}
      {gameStage === 'game' && <Game verifyLetter={verifyLetter}/>}
      {gameStage === 'end' && <GameOver retry={retry}/>}
    </div>

  );
};

export default App;
