import {useState, usere} from 'react'
import "./Game.css"

const Game = ({verifyLetter, pickedWord, pickedCategory, letters, guessedLetters, wrongLetters, guesses, score}) => {

    const [letter, setLetter] = useState()

    const handleSubmit = (e) => {
        e.preventDefault();

        verifyLetter(letter)

        setLetter("")
    }

    return (
        <div className="game">
            <p className="points">
                <span>Pontiuação: {score}</span>
            </p>
            <h1>Advinhe a palavra</h1>
            <h3 className="tip">Dica sobre a palavra:   <span>{pickedCategory}</span></h3>
            <p>Você ainda tem {guesses} tentativa(s).</p>
            <div className="wordContainer">
                {letters.map((letter, i) => (
                    guessedLetters.includes(letter) ? (
                        <span key={i} className="letter">{letter}</span>
                    ) : (
                        <span key={i} className="blankSquare"></span>
                    )
                ))}
            </div>
            <div className="letterContainer">
                <p>Tente advinhar uma letra da palavra</p>
                <form onSubmit={handleSubmit} >
                    <input type="text" name="letter" maxLength="1" required onChange={(e) => setLetter(e.target.value)} value={letter} />
                    <button>Jogar!</button>
                </form>
            </div>
            <div className="wrongLettersContainer">
                <p>Letras já ultilizadas:   </p>
                {wrongLetters.map((letter, i) => (
                    <span key={i}>{letter}, </span>
                ))}
            </div>
        </div>
    )
}

export default Game
